# automated way to convert licenses to their names from the CSV
# https://learn.microsoft.com/en-us/azure/active-directory/enterprise-users/licensing-service-plan-reference
<#
$Data = Invoke-WebRequest -Uri 'https://download.microsoft.com/download/e/3/e/e3e9faf2-f28b-490a-9ada-c6089a1fc5b0/Product%20names%20and%20service%20plan%20identifiers%20for%20licensing.csv' -OutFile "C:\Temp\licensing.csv"
$CSV = Import-Csv -Path "C:\Temp\licensing.csv" -Delimiter ',' -Encoding UTF8
$CSVHashReverted = [ordered] @{}
$CSVHash = [ordered] @{}
foreach ($C in $CSV) {
    $CSVHash[$C.String_ID] = $C.Product_Display_Name
    if (-not $CSVHash[$C.Service_Plan_Name]) {
        $CSVHash[$C.Service_Plan_Name] = $C.Service_Plans_Included_Friendly_Names
    }
    $CSVHashReverted[$C.Product_Display_Name] = $C.String_ID
    if (-not $CSVHashReverted[$C.Service_Plans_Included_Friendly_Names]) {
        $CSVHashReverted[$C.Service_Plans_Included_Friendly_Names] = $C.Service_Plan_Name
    }
}
$CSVServiceHash = [ordered] @{}
foreach ($C in $CSV) {
    $CSVServiceHash[$C.Service_Plan_Name] = $C.Service_Plans_Included_Friendly_Names
}
$Output = @(
    "[ordered] @{"
    foreach ($License in $CSVHash.Keys | Sort-Object) {
        $LicenseName = $CSVHash[$License]
        "   '$($License)' = `"$($LicenseName)`""
    }
    "}"
)
$Output -Join "`r`n" | Set-Clipboard

$OutputReverted = @(
    "[ordered] @{"
    foreach ($License in $CSVHashReverted.Keys | Sort-Object) {
        $LicenseName = $CSVHashReverted[$License]
        "   '$($License)' = `"$($LicenseName)`""
    }
    "}"
)
$OutputReverted -Join "`r`n" | Set-Clipboard
#>